function email(){var b=$("#form-private");showerrors(b);b.validate({rules:{email:{required:true,uniqueemail:true}},messages:{email:{required:"Por favor, escriba el correo.",}}});
if(b.valid()){var c=b.attr("data-action");var a=sendformajax(c,"usuario",b);switch(a[0]){case true:redirectpage("mi-perfil");break;case false:redirectpage("modificar-correo");
break;}}else{return false;}return false;}$(document).ready(function(){$("form button").click(function(){email();});});